import React, { useRef, useEffect } from 'react';
import styled from 'styled-components';

const VideoContainer = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
  background: #000;
  position: relative;
  border-radius: 8px;
  overflow: hidden;
  flex: 1;
  min-height: 0;
`;

const VideoWrapper = styled.div`
  width: 100%;
  height: 100%;
  position: relative;
  display: flex;
  flex-direction: column;
  overflow: visible;
`;

const VideoElement = styled.video`
  max-width: 100%;
  max-height: 100%;
  object-fit: contain;
  border-radius: 8px;
`;

const VideoControls = styled.div`
  position: absolute;
  bottom: 20px;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  gap: ${({ theme }) => theme.spacing.sm};
  background: rgba(0, 0, 0, 0.8);
  padding: ${({ theme }) => theme.spacing.sm};
  border-radius: ${({ theme }) => theme.borderRadius.md};
  opacity: 1;
  transition: opacity 0.3s ease;
  backdrop-filter: blur(10px);
  z-index: 20;
`;

const ControlButton = styled.button`
  width: 32px;
  height: 32px;
  border: none;
  background: transparent;
  color: white;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 16px;
  border-radius: 4px;

  &:hover {
    background: rgba(255, 255, 255, 0.2);
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const TimeDisplay = styled.div`
  color: white;
  font-size: 12px;
  font-family: monospace;
  display: flex;
  align-items: center;
  padding: 0 ${({ theme }) => theme.spacing.sm};
`;

const ProgressBar = styled.div<{ $isDragging?: boolean }>`
  position: absolute;
  bottom: -6px;
  left: 0;
  right: 0;
  height: 6px;
  background: rgba(255, 255, 255, 0.2);
  cursor: pointer;
  border-radius: 0 0 8px 8px;
  z-index: 10;
  pointer-events: auto;
  transition: height 0.1s ease;

  ${({ $isDragging }) => $isDragging && `
    height: 8px;
    background: rgba(255, 255, 255, 0.3);
  `}
`;

const ProgressFill = styled.div<{ $progress: number }>`
  height: 100%;
  width: ${({ $progress }) => $progress}%;
  background: ${({ theme }) => theme.colors.primary};
  transition: width 0.1s ease;
  position: relative;

  &::after {
    content: '';
    position: absolute;
    right: -4px;
    top: 50%;
    transform: translateY(-50%);
    width: 8px;
    height: 8px;
    background: ${({ theme }) => theme.colors.primary};
    border-radius: 50%;
    opacity: 0;
    transition: opacity 0.2s ease;
  }

  &:hover::after {
    opacity: 1;
  }
`;

interface VideoPreviewProps {
  videoUrl: string;
  currentTime: number;
  isPlaying: boolean;
  onTimeUpdate?: (time: number) => void;
  isTimelineControlled?: boolean;
}

const VideoPreview: React.FC<VideoPreviewProps> = ({
  videoUrl,
  currentTime,
  isPlaying,
  onTimeUpdate,
  isTimelineControlled = true
}) => {
  const videoRef = useRef<HTMLVideoElement>(null);
  const [duration, setDuration] = React.useState(0);
  const [currentVideoTime, setCurrentVideoTime] = React.useState(0);
  const [isDragging, setIsDragging] = React.useState(false);
  const [dragTime, setDragTime] = React.useState(0);

  // Sync video with timeline (when timeline changes)
  useEffect(() => {
    if (videoRef.current && isTimelineControlled) {
      videoRef.current.currentTime = currentTime;
      setCurrentVideoTime(currentTime);
    }
  }, [currentTime, isTimelineControlled]);

  // Sync play state
  useEffect(() => {
    if (videoRef.current && isTimelineControlled) {
      if (isPlaying) {
        videoRef.current.play().catch(console.error);
      } else {
        videoRef.current.pause();
      }
    }
  }, [isPlaying, isTimelineControlled]);

  // Cleanup event listeners on unmount
  useEffect(() => {
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, []);

  const handleTimeUpdate = () => {
    if (videoRef.current) {
      const newTime = videoRef.current.currentTime;
      setCurrentVideoTime(newTime);
      // Notify parent component of time update only when timeline controlled
      if (onTimeUpdate && isTimelineControlled) {
        onTimeUpdate(newTime);
      }
    }
  };

  const handleLoadedMetadata = () => {
    if (videoRef.current) {
      setDuration(videoRef.current.duration);
    }
  };

  const handlePlayPause = () => {
    if (!isTimelineControlled && videoRef.current) {
      if (videoRef.current.paused) {
        videoRef.current.play().catch(console.error);
      } else {
        videoRef.current.pause();
      }
    }
  };

  const handleSeek = (event: React.MouseEvent<HTMLDivElement>) => {
    if (videoRef.current && duration > 0) {
      const rect = event.currentTarget.getBoundingClientRect();
      const clickX = event.clientX - rect.left;
      const percentage = clickX / rect.width;
      const newTime = percentage * duration;
      videoRef.current.currentTime = newTime;
      setCurrentVideoTime(newTime);
      // Notify parent component only when timeline controlled
      if (onTimeUpdate && isTimelineControlled) {
        onTimeUpdate(newTime);
      }
    }
  };

  const handleMouseDown = (event: React.MouseEvent<HTMLDivElement>) => {
    if (videoRef.current && duration > 0) {
      setIsDragging(true);
      const rect = event.currentTarget.getBoundingClientRect();
      const clickX = event.clientX - rect.left;
      const percentage = clickX / rect.width;
      const newTime = percentage * duration;
      setDragTime(newTime);

      // Add global mouse event listeners
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    }
  };

  const handleMouseMove = (event: MouseEvent) => {
    if (isDragging && videoRef.current && duration > 0) {
      const progressBar = event.target as HTMLElement;
      const rect = progressBar.getBoundingClientRect();
      const mouseX = event.clientX - rect.left;
      const percentage = Math.max(0, Math.min(1, mouseX / rect.width));
      const newTime = percentage * duration;
      setDragTime(newTime);
    }
  };

  const handleMouseUp = () => {
    if (isDragging && videoRef.current) {
      setIsDragging(false);
      videoRef.current.currentTime = dragTime;
      setCurrentVideoTime(dragTime);

      // Notify parent component only when timeline controlled
      if (onTimeUpdate && isTimelineControlled) {
        onTimeUpdate(dragTime);
      }

      // Remove global event listeners
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    }
  };

  const formatTime = (seconds: number) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const progress = duration > 0 ? ((isDragging ? dragTime : currentVideoTime) / duration) * 100 : 0;

  return (
    <VideoWrapper>
      <VideoContainer>
        <VideoElement
          ref={videoRef}
          src={videoUrl}
          onTimeUpdate={handleTimeUpdate}
          onLoadedMetadata={handleLoadedMetadata}
          muted
        />

        <VideoControls>
          <ControlButton onClick={handlePlayPause} disabled={isTimelineControlled}>
            {isPlaying ? '⏸️' : '▶️'}
          </ControlButton>
          <TimeDisplay>
            {formatTime(currentVideoTime)} / {formatTime(duration)}
          </TimeDisplay>
        </VideoControls>
      </VideoContainer>

      <ProgressBar onMouseDown={handleMouseDown} $isDragging={isDragging}>
        <ProgressFill $progress={progress} />
      </ProgressBar>
    </VideoWrapper>
  );
};

export default VideoPreview;
